mapper.select:
select T.* from ( select rownum as RNO, V.seq, V.name, V.subject, V.content, V.readcount, V.regdate, V.commentCount from ( select seq, name, case when length(subject) > 20 then substr(subject, 1, 18) || '..' else subject end as subject , content, readcount, to_char(regdate, 'yyyy-mm-dd hh24:mi:ss') as regdate , commentCount from tblBoard where status = 1
and ${colname} like '%' || #{search} || '%'
order by seq desc )V )T where T.RNO >= #{start} and T.RNO <= #{end}

select seq, name, subject, content, readcount, to_char(regdate, 'yyyy-mm-dd hh24:mi:ss') as regdate
from tblBoard
where status = 1 and seq = #{seq}

select count(*)
from tblBoard
where seq = #{seq} and pw = #{pw}

select count(*)
from tblComment
where parentSeq = #{seq}
